    static class Solution {
        public int countBinarySubstrings(String s) {
            int one = 0;
           int zero =0;
            int result =0;

            for( int i=0;i<s.length();i++){
                for ( int j=i;j<s.length();j++){

                    if (s.charAt(j) =='0') one++;
                    if (s.charAt(j) =='1') zero++;

                    if (one == zero) {
                        result++;
                        one = 0;
                        zero =0;
                        break;
                    }

                }
                one = 0;
                zero =0;

            }

            return result;
        }
    }
